// withStyles.js
import React, { useEffect, useState } from 'react';

const withStyles = (WrappedComponent, styles) => {
  return (props) => {
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
      const promises = styles.map((style) => import(style));
      Promise.all(promises).then(() => setLoaded(true));
    }, [styles]);

    return loaded ? <WrappedComponent {...props} /> : null;
  };
};

export default withStyles;
